import React, {Component} from 'react';
import ProjectDataService from '../service/ProjectDataService';
import { Formik, Form, Field, ErrorMessage } from 'formik';


class IssueComponent extends Component{

    constructor(props){
        super(props)

        this.state={
            id: this.props.match.params.id,
            name: null,
            description: null,
            iid: null
        }
    }

    componentDidMount(){
        console.log("JESTEM W DID MOUNT ISSUE COMPONENt")
        if(this.state.id==-1){
            return
        }

        ProjectDataService.retrieveIssue(this.state.id)
            .then(response=> this.setState({
                name: response.data.name,
                description: response.data.description,
                iid: response.data.iid

            }))

            console.log("name="+this.state.name)
            console.log("description="+this.state.description)
            console.log("iid="+this.state.iid)



    }

    render(){

        let {name,id,iid,description}=this.state

        return (
            <div>
                <h3>Issue</h3>
                <div className="container">
                    <Formik
                        initialValues={{ id, description,name }}
                    >
                        {
                            (props) => (
                                <Form>
                                    <fieldset className="form-group">
                                        <label>Id</label>
                                        <Field className="form-control" type="text" name="id" disabled />
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Name</label>
                                        <Field className="form-control" type="text" name="name" />
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Description</label>
                                        <Field className="form-control" type="text" name="description" />
                                    </fieldset>
                                    <button className="btn btn-success" type="submit">Save</button>
                                </Form>
                            )
                        }
                    </Formik>

                </div>
            </div>
        )
    }
}

export default IssueComponent
